import React, { useState } from 'react';

export default function EventForm({ onAdd }) {
  const [title, setTitle] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [location, setLocation] = useState('');
  const [description, setDescription] = useState('');

  const submit = (e) => {
    e.preventDefault();
    if (!title || !startDate || !endDate) {
      return alert('Title, start date & end date are required');
    }
    if (new Date(startDate) > new Date(endDate)) {
      return alert('End time must be after start time');
    }

    onAdd({ title, startDate, endDate, location, description });

    // reset form
    setTitle('');
    setStartDate('');
    setEndDate('');
    setLocation('');
    setDescription('');
  };

  return (
    <form onSubmit={submit} style={{
      display: 'grid',
      gap: '12px',
      padding: '16px',
      border: '1px solid #ddd',
      borderRadius: '8px',
      background: '#fafafa'
    }}>
      <input
        placeholder="Event Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
        style={{ padding: '8px' }}
      />

      <div style={{ display: 'flex', gap: '12px' }}>
        <label style={{ flex: 1 }}>
          Start:
          <input type="datetime-local" value={startDate} onChange={e => setStartDate(e.target.value)} style={{ width: '100%', padding: '6px' }} />
        </label>
        <label style={{ flex: 1 }}>
          End:
          <input type="datetime-local" value={endDate} onChange={e => setEndDate(e.target.value)} style={{ width: '100%', padding: '6px' }} />
        </label>
      </div>

      <input
        placeholder="Location"
        value={location}
        onChange={e => setLocation(e.target.value)}
        style={{ padding: '8px' }}
      />

      <textarea
        placeholder="Description"
        value={description}
        onChange={e => setDescription(e.target.value)}
        style={{ padding: '8px', minHeight: '60px' }}
      />

      <button type="submit" style={{
        padding: '10px',
        background: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '6px',
        cursor: 'pointer'
      }}>Add Event</button>
    </form>
  );
}
