import React from 'react';

function fmtDate(d){
  try{
    const dt = new Date(d);
    return dt.toLocaleString();
  }catch(e){ return d; }
}

export default function EventList({ events, onDelete }){
  if(!events || events.length===0) return <p>No events yet</p>;
  return (
    <ul style={{ listStyle:'none', padding:0 }}>
      {events.map(ev=>(
        <li key={ev._id} style={{ border:'1px solid #ddd', padding:12, marginBottom:8 }}>
          <h3>{ev.title}</h3>
          <div><strong>Date:</strong> {fmtDate(ev.date)}</div>
          {ev.location && <div><strong>Location:</strong> {ev.location}</div>}
          {ev.description && <p>{ev.description}</p>}
          <button onClick={()=>onDelete(ev._id)}>Delete</button>
        </li>
      ))}
    </ul>
  );
}
